const shortestPath = (grid, k) => {
    const m = grid.length;
    const n = grid[0].length;
    if (k >= m + n - 3) {
        return m + n - 2;
    }
    const q = [[0, 0, k]];
    const vis = new Set([[0, 0, k]]);
    let ans = 0;
    while (q.length) {
        ans += 1;
        const len = q.length;
        for (let i = 0; i < len; i++) {
            const [i, j, k] = q.shift();
            for (const [a, b] of [[0, -1], [0, 1], [1, 0], [-1, 0]]) {
                const x = i + a;
                const y = j + b;
                if (0 <= x && x < m && 0 <= y && y < n) {
                    if (x === m - 1 && y === n - 1) {
                        return ans;
                    }
                    if (grid[x][y] === 0 && !vis.has([x, y, k])) {
                        q.push([x, y, k]);
                        vis.add([x, y, k]);
                    }
                    if (grid[x][y] === 1 && k > 0 && !vis.has([x, y, k - 1])) {
                        q.push([x, y, k - 1]);
                        vis.add([x, y, k - 1]);
                    }
                }
            }
        }
    }
    return -1;
};
