function find_maximum_inform_time(n, headID, manager, informTime) {
    function dfs(i) {
        var max_inform_time = 0;
        for (var j = 0; j < subordinates[i].length; j++) {
            max_inform_time = Math.max(max_inform_time, informTime[i] + dfs(subordinates[i][j]));
        }
        return max_inform_time;
    }
    var subordinates = [];
    for (var i = 0; i < n; i++) {
        subordinates.push([]);
    }
    for (var i = 0; i < n; i++) {
        subordinates[manager[i]] = subordinates[manager[i]] || [];
        subordinates[manager[i]].push(i);
    }
    return dfs(headID);
}
