// decode a string encoded in a specific pattern
function f_gold(encodedText, rows) {
    var ans = [];
    // calculate the number of columns
    var cols = Math.floor(encodedText.length / rows);
    // iterate through each column
    for (var j = 0; j < cols; j++) {
        var x = 0;
        var y = j;
        // iterate through each row in the current column
        while (x < rows && y < cols) {
            ans.push(encodedText[x * cols + y]);
            x = x + 1;
            y = y + 1;
        }
    }
    // join the decoded characters and remove any leading/trailing white space
    return ans.join('').trim();
}
```
