// check if the input array is sorted in non-decreasing or non-increasing order
function f_gold(nums) {
    var isIncr = false, isDecr = false;
    for (var i = 0; i < nums.length - 1; i++) {
        if (nums[i + 1] < nums[i]) {
            isIncr = true; // if the next element is smaller, set isIncr to true
        }
        else if (nums[i + 1] > nums[i]) {
            isDecr = true; // if the next element is larger, set isDecr to true
        }
        if (isIncr && isDecr) {
            return false; // if both isIncr and isDecr are true, the array is not sorted
        }
    }
    return true; // if the loop completes, the array is sorted
}
