```javascript
function findEarliestDayToMakeBouquets(bloomDay, numberOfBouquets, flowersPerBouquet) {
    if (numberOfBouquets * flowersPerBouquet > bloomDay.length) return -1;
    function check(day) {
        var count = 0, current = 0;
        for (var bloom of bloomDay) {
            current = (bloom <= day) ? current + 1 : 0;
            if (current == flowersPerBouquet) {
                count += 1;
                current = 0;
            }
        }
        return count >= numberOfBouquets;
    }
    var earliestDay = Math.min(...bloomDay);
    var latestDay = Math.max(...bloomDay);
    while (earliestDay < latestDay) {
        var middleDay = (earliestDay + latestDay) >> 1;
        if (check(middleDay)) latestDay = middleDay;
        else earliestDay = middleDay + 1;
    }
    return earliestDay;
}
```
