def f_gold(arr: List[int]) -> List[int]:
    def find(cnt):
        s = 0
        for i, v in enumerate(arr):
            s += v
            if s == cnt:
                return i
        return -1
    n = len(arr)
    cnt, mod = divmod(sum(arr), 3)
    if mod:
        return [-1, -1]
    if cnt == 0:
        return [0, n - 1]
