    j = find(cnt + 1)
    k = find(cnt * 2 + 1)
    while k < n and arr[i] == arr[j] == arr[k]:
        i, j, k = i + 1, j + 1, k + 1
    if k == n:
        return [i - 1, j]
    return [-1, -1]
