```javascript
function check_points_on_line(points) {
    var min_x = Number.POSITIVE_INFINITY;
    var max_x = Number.NEGATIVE_INFINITY;
    var point_set = new Set();
    for (var i = 0; i < points.length; i++) {
        var x_coordinate = points[i][0];
        var y_coordinate = points[i][1];
        min_x = Math.min(min_x, x_coordinate);
        max_x = Math.max(max_x, x_coordinate);
        point_set.add(String([x_coordinate, y_coordinate]));
    }
    var sum_of_min_max_x = min_x + max_x;
    for (var i = 0; i < points.length; i++) {
        var x_coordinate = points[i][0];
        var y_coordinate = points[i][1];
        if (!point_set.has(String([sum_of_min_max_x - x_coordinate, y_coordinate]))) {
            return false;
        }
    }
    return true;
}
```
