// function to calculate the number of days in a given month of a year
function f_gold(year, month) {
    // check if the year is a leap year
    var leap = (year % 4 == 0 && year % 100 != 0) || (year % 400 == 0);
    // array of days in each month, with February adjusted for leap years
    var days = [0, 31, leap ? 29: 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31];
    // return the number of days in the given month
    return days[month];
}
