// define a function that takes an array of numbers as input and returns an index
// the index is the position in the array where the absolute difference between the average of the elements to the left and the average of the elements to the right is minimized
function f_gold(nums) {
    // calculate the cumulative sum of the array
    let s = nums.map((sum => value => sum += value)(0));
    let ans = 0;
    let n = nums.length;
    let mi = Number.MAX_VALUE;
    // iterate through the array and calculate the absolute difference between the averages of the elements to the left and right of the current index
    for (let i = 0; i < n; i++) {
        let a = Math.floor(s[i] / (i + 1));
        let b = 0;
        if (i != n - 1) {
            b = Math.floor((s[s.length - 1] - s[i]) / (n - i - 1));
        }
        let t = Math.abs(a - b);
        // update the index and minimum difference if the current difference is smaller than the previous minimum
        if (mi > t) {
            ans = i;
            mi = t;
        }
    }
    // return the index with the minimum difference
    return ans;
}
