// function to find the sum of all subarrays of an array and return the sum of elements in a given range
function f_gold(nums, n, left, right) {
    var arr = [];
    // loop through all possible subarrays and add their sum to arr
    for (var i = 0; i < n; i++) {
        var s = 0;
        for (var j = i; j < n; j++) {
            s += nums[j];
            arr.push(s);
        }
    }
    // sort arr in ascending order
    arr.sort((a, b) => a - b);
    // set MOD to 10^9 + 7
    var MOD = Math.pow(10, 9) + 7;
    // return the sum of elements in the range left to right (inclusive) of arr, modulo MOD
    return arr.slice(left - 1, right).reduce(function (a, b) { return a + b; }) % MOD;
}
