// function to find the earliest day to make m bouquets, each bouquet consisting of k adjacent flowers
function f_gold(bloomDay, m, k) {
    // if there are not enough flowers to make m bouquets, return -1
    if (m * k > bloomDay.length) return -1;
    // helper function to check if it's possible to make m bouquets with the given day
    function check(day) {
        var cnt = 0, cur = 0;
        for (var bd of bloomDay) {
            cur = (bd <= day) ? cur + 1 : 0;
            if (cur == k) {
                cnt += 1;
                cur = 0;
            }
        }
        return cnt >= m;
    }
    // binary search to find the earliest day
    var left = Math.min(...bloomDay);
    var right = Math.max(...bloomDay);
    while (left < right) {
        var mid = (left + right) >> 1;
        if (check(mid)) right = mid;
        else left = mid + 1;
    }
    return left;
}
