function find_min_flips_to_make_binary_string_balanced(s) {
    let n = s.length;
    let left_count = new Array(n + 1).fill(0);
    let right_count = new Array(n + 1).fill(0);
    let min_flips = 0x3F3F3F3F;
    for (let i = 1; i < n + 1; i++) {
        left_count[i] = left_count[i - 1] + (s[i - 1] == '1' ? 1 : 0);
    }
    for (let i = n - 1; i > -1; i--) {
        right_count[i] = right_count[i + 1] + ((s[i] == '0') ? 1 : 0);
    }
    for (let i = 0; i < n + 1; i++) {
        min_flips = Math.min(min_flips, left_count[i] + right_count[i]);
    }
    return min_flips;
}
