function calculate_gold(bank_transactions) {
    let last_transaction_count = 0, total_gold = 0;
    for (let transaction of bank_transactions) {
        var transaction_count;
        if ((transaction_count = transaction.split("1").length - 1) > 0) {
            total_gold += last_transaction_count * transaction_count;
            last_transaction_count = transaction_count;
        }
    }
