"use strict";

function minTimeToType(word) {
    let ans = 0;
    let prev = 0;
    for (let i = 0; i < word.length; i++) {
        let curr = word.charCodeAt(i) - 'a'.charCodeAt(0);
        let t = Math.abs(prev - curr);
        t = Math.min(t, 26 - t);
        ans += t + 1;
        prev = curr;
    }
    return ans;
}
