function find_minimal_cost(price_list, special_offers, needs_list) {
    function calculate_total_cost(price_list, needs_list) {
        return needs_list.map((needs, i) => price_list[i] * needs).reduce((a, b) => a + b, 0);
    }
    var total_cost = calculate_total_cost(price_list, needs_list);
    var remaining_needs = [];
    for (let offer of special_offers) {
        remaining_needs.length = 0;
        for (let j = 0; j < needs_list.length; j++) {
            if (offer[j] > needs_list[j]) {
                remaining_needs.length = 0;
                break;
            }
            remaining_needs.push(needs_list[j] - offer[j]);
        }
        if (remaining_needs.length > 0) {
            total_cost = Math.min(total_cost, offer.at(-1) + find_minimal_cost(price_list, special_offers, remaining_needs));
        }
    }
    return total_cost;
}
