function find_longest_palindrome(s) {
    var n = s.length;
    var char_count = {};
    for (var i = 0; i < n; i++) {
        if (char_count[s[i]]) {
            char_count[s[i]] += 1;
        } else {
            char_count[s[i]] = 1;
        }
    }
    var odd_char_count = 0;
    for (var key in char_count) {
        if (char_count[key] % 2 == 1) {
            odd_char_count += 1;
        }
    }
    return (odd_char_count == 0) ? n : n - odd_char_count + 1;
}
