```javascript
function find_maximum_value(nums) {
    var length = nums.length;
    if (length == 1) {
        return nums[0];
    }
    var temp_array = [];
    for (var i = 0; i < (length >> 1); i++) {
        var value = ((i & 1) ?
            Math.max(nums[i << 1], nums[i << 1 | 1]) :
            Math.min(nums[i << 1], nums[i << 1 | 1]));
        temp_array.push(value);
    }
    return find_maximum_value(temp_array);
}
