function find_maximum_product(nums) {
    var mod = Math.pow(10, 9) + 7;
    var n = nums.length;
    var left = Array(n).fill(-1);
    var right = Array(n).fill(n);
    var stack_left = [];
    for (var i = 0; i < n; i++) {
        while (stack_left.length > 0 && nums[stack_left[stack_left.length - 1]] >= nums[i]) {
            stack_left.pop();
        }
        if (stack_left.length > 0) {
            left[i] = stack_left[stack_left.length - 1];
        }
        stack_left.push(i);
    }
    var stack_right = [];
    for (var i = n - 1; i >= 0; i--) {
        while (stack_right.length > 0 && nums[stack_right[stack_right.length - 1]] > nums[i]) {
            stack_right.pop();
        }
        if (stack_right.length > 0) {
            right[i] = stack_right[stack_right.length - 1];
        }
        stack_right.push(i);
    }
    var prefix_sum = [0].concat(nums.map((sum => value => sum += value)(0)));
    var max_product = Math.max.apply(Math, nums.map(function (v, i) { return v * (prefix_sum[right[i]] - prefix_sum[left[i] + 1]); }));
    return max_product % mod;
}
