function convertStringToInteger(s) {
    if (!s) return 0;
    var stringLength = s.length;
    if (stringLength == 0) return 0;
    var i = 0;
    while (s[i] == ' ') {
        i++;
        if (i == stringLength) return 0;
    }
    var sign = -1;
    if (s[i] == '-') sign = -1;
    else sign = 1;
    if (s[i] == '-' || s[i] == '+') i++;
    var result = 0;
    var flag = Math.floor((Math.pow(2, 31) - 1) / 10);
    while (i < stringLength) {
        if (!s[i].match(/\d/)) break;
        var c = parseInt(s[i]);
        if (result > flag || (result == flag && c > 7)) {
            if (sign < 0) return -(Math.pow(2, 31)); 
            else return Math.pow(2, 31) - 1;
        }
        result = result * 10 + c;
        i++;
    }
    return sign * result;
}
