function f_gold(nums, n, left, right) {
    let arr = [];
    for (let i = 0; i < n; i++) {
        let s = 0;
        for (let j = i; j < n; j++) {
            s += nums[j];
            arr.push(s);
        }
    }
    arr.sort((a, b) => a - b);
    const MOD = 10**9 + 7;
    return arr.slice(left - 1, right).reduce((a, b) => a + b, 0) % MOD;
}
