function find_index_with_min_avg_difference(nums) {
    let prefix_sum = nums.map((sum => value => sum += value)(0));
    let ans = 0;
    let n = nums.length;
    let min_difference = Number.MAX_VALUE;
    for (let i = 0; i < n; i++) {
        let avg_left = Math.floor(prefix_sum[i] / (i + 1));
        let avg_right = 0;
        if (i != n - 1) {
            avg_right = Math.floor((prefix_sum[prefix_sum.length - 1] - prefix_sum[i]) / (n - i - 1));
        }
        let difference = Math.abs(avg_left - avg_right);
        if (min_difference > difference) {
            ans = i;
            min_difference = difference;
        }
    }
    return ans;
}
