// function to count the number of contiguous subarrays whose sum is divisible by k
function f_gold(nums, k) {
    var ans = 0, s = 0;
    // initialize a counter object with 0 as key and 1 as value
    var counter = { 0: 1 };
    // loop through the array of numbers
    for (var i = 0; i < nums.length; i++) {
        // calculate the cumulative sum
        s += nums[i];
        // check if the remainder of the cumulative sum divided by k is in the counter object
        // if it is, add the value of the counter object to the answer
        ans += s % k in counter ? counter[s % k] : 0;
        // increment the counter object for the remainder of the cumulative sum divided by k
        counter[s % k] = s % k in counter ? counter[s % k] + 1 : 1;
    }
    // return the answer
    return ans;
}
