// function to change the color of a pixel and all its connected pixels in an image
function f_gold(image, sr, sc, newColor) {
    // helper function to perform depth-first search
    function dfs(i, j, oc, nc) {
        // base case: if out of bounds or not the original color or already the new color, return
        if (i < 0 || i >= image.length || j < 0 || j >= image[0].length || image[i][j] != oc || image[i][j] == nc) {
            return;
        }
        // change the color of the current pixel
        image[i][j] = nc;
        // recursively call dfs on the neighboring pixels
        for (let [x, y] of [[0, 1], [0, -1], [1, 0], [-1, 0]]) {
            dfs(i + x, j + y, oc, nc);
        }
    }
    // call dfs on the starting pixel with the original color and the new color
    dfs(sr, sc, image[sr][sc], newColor);
    // return the modified image
    return image;
}
