function find_triplet(triplets, target) {
    var max_a = 0, max_b = 0, max_c = 0;
    var [target_a, target_b, target_c] = target;
    for (var i = 0; i < triplets.length; i++) {
        var a = triplets[i][0];
        var b = triplets[i][1];
        var c = triplets[i][2];
        if (a <= target_a && b <= target_b && c <= target_c) {
            max_a = Math.max(max_a, a);
            max_b = Math.max(max_b, b);
            max_c = Math.max(max_c, c);
        }
    }
    return max_a == target_a && max_b == target_b && max_c == target_c;
}
