// function to check if it is possible to finish all courses with given prerequisites
function f_gold(numCourses, prerequisites) {
    // create a map to store edges between courses and an array to store indegrees of courses
    let edges = new Map();
    let indegree = new Array(numCourses).fill(0);
    // iterate through prerequisites to create edges and calculate indegrees
    for (let i = 0; i < prerequisites.length; i++) {
        let a = prerequisites[i][0];
        let b = prerequisites[i][1];
        if (edges.has(b)) {
            edges.get(b).push(a);
        } else {
            edges.set(b, [a]);
        }
        indegree[a] += 1;
    }
    // create a queue to store courses with indegree 0
    let q = [];
    for (let i = 0; i < numCourses; i++) {
        if (indegree[i] == 0) {
            q.push(i);
        }
    }
    // iterate through the queue to update indegrees and add new courses with indegree 0
    let n = 0;
    while (q.length > 0) {
        let b = q.shift();
        n += 1;
        for (let i = 0; i < edges.get(b)?.length; i++) {
            let a = edges.get(b)[i];
            indegree[a] -= 1;
            if (indegree[a] == 0) {
                q.push(a);
            }
        }
    }
    // return true if all courses can be finished, false otherwise
    return n == numCourses;
}
