function find_minimum_time(tasks) {
    let task_count = new Map();
    for (let i = 0; i < tasks.length; i++) {
        if (task_count.has(tasks[i])) {
            task_count.set(tasks[i], task_count.get(tasks[i]) + 1);
        } else {
            task_count.set(tasks[i], 1);
        }
    }
    let minimum_count = Number.MAX_SAFE_INTEGER;
    for (let [key, value] of task_count) {
        if (value < minimum_count) {
            minimum_count = value;
        }
    }
    if (minimum_count == 1) {
        return -1;
    }
    let total_time = 0;
    for (let [key, value] of task_count) {
        total_time += Math.floor(value / 3) + ((value % 3 == 0) ? 0 : 1);
    }
    return total_time;
}
