function find_segments_intersection(segments) {
    var segment_map = {};
    for (var i = 0; i < segments.length; i++) {
        if (!(segments[i][0] in segment_map)) {
            segment_map[segments[i][0]] = 0;
        }
        segment_map[segments[i][0]] += segments[i][2];
        if (!(segments[i][1] in segment_map)) {
            segment_map[segments[i][1]] = 0;
        }
        segment_map[segments[i][1]] -= segments[i][2];
    }
    var sorted_segments = [];
    for (var key in segment_map) {
        sorted_segments.push([parseInt(key), segment_map[key]]);
    }
    sorted_segments.sort(function (a, b) {
        return a[0] == b[0] ? a[1] - b[1] : a[0] - b[0];
    });
    var n = sorted_segments.length;
    for (var i = 1; i < n; i++) {
        sorted_segments[i][1] += sorted_segments[i - 1][1];
    }
    var result = [];
    for (var i = 0; i < n - 1; i++) {
        if (sorted_segments[i][1]) {
            result.push([sorted_segments[i][0], sorted_segments[i + 1][0], sorted_segments[i][1]]);
        }
    }
    return result;
}
