function find_minimum_subarray_length(nums, k) {
    var prefix_sum = [0].concat(nums.map((sum => value => sum += value)(0)));
    var min_length = Number.POSITIVE_INFINITY;
    var queue = [0];
    for (var i = 1; i < prefix_sum.length; i++) {
        while (queue.length && prefix_sum[i] - prefix_sum[queue[0]] >= k) {
            min_length = Math.min(min_length, i - queue.shift());
        }
        while (queue.length && prefix_sum[i] <= prefix_sum[queue[queue.length - 1]]) {
            queue.pop();
        }
        queue.push(i);
    }
    return (min_length == Number.POSITIVE_INFINITY) ? -1 : min_length;
}
