// function to calculate the sum of pairwise bit differences between all numbers in an array
function f_gold(nums) {
    var ans = 0;
    // iterate over each bit position
    for (var i = 0; i < 31; i++) {
        var b = 0;
        var a = b;
        // iterate over each number in the array
        for (var v of nums) {
            // get the bit value at the current position
            var t = (v >> i) & 1;
            // increment the count of numbers with a 1 bit at the current position
            if (t) {
                a += 1;
            }
            // increment the count of numbers with a 0 bit at the current position
            else {
                b += 1;
            }
        }
        // add the pairwise bit differences for the current position to the total sum
        ans += a * b;
    }
    // return the total sum of pairwise bit differences
    return ans;
}
