function f_gold(sentence1, sentence2, similarPairs) {
    if (sentence1.length !== sentence2.length) {
        return false;
    }
    const pairs = new Set(similarPairs.map(pair => pair.join(",")));
    for (let i = 0; i < sentence1.length; i++) {
        const similar = pairs.has(`${sentence1[i]},${sentence2[i]}`)
            || pairs.has(`${sentence2[i]},${sentence1[i]}`)
            || sentence1[i] === sentence2[i];
        if (!similar) {
            return false;
        }
    }
    return true;
}
