// function to find the minimum number of jumps required to reach the end of an array
function f_gold(nums) {
    let end = 0, mx = 0, steps = 0;
    for (let i = 0; i < nums.length - 1; i++) {
        mx = Math.max(mx, i + nums[i]); // update the maximum reachable index
        if (i == end) { // if we have reached the end of the current jump
            end = mx; // update the end index for the next jump
            steps += 1; // increment the number of jumps
        }
    }
    return steps; // return the minimum number of jumps required
}
```
