function findSubstringInString(string, substring, mappings) {
    let charMap = new Map();
    for (let i = 0; i < mappings.length; i++) {
        let charA = mappings[i][0];
        let charB = mappings[i][1];
        if (charMap.has(charA)) {
            charMap.get(charA).add(charB);
        }
        else {
            let charSet = new Set();
            charSet.add(charB);
            charMap.set(charA, charSet);
        }
    }
    let stringLength = string.length;
    let substringLength = substring.length;
    for (let i = 0; i < stringLength - substringLength + 1; i++) {
        let flag = true;
        for (let j = 0; j < substringLength; j++) {
            let charA = string[i + j];
            let charB = substring[j];
            if (charA == charB || (charMap.has(charB) && charMap.get(charB).has(charA))) {
                continue;
            }
            else {
                flag = false;
                break;
            }
        }
        if (flag) {
            return true;
        }
    }
    return false;
}
