function findOrderOfCourses (numCourses, prerequisites) {
    var edges = new Map();
    var indegree = new Array(numCourses).fill(0);
    for (var i = 0; i < prerequisites.length; i++) {
        var course = prerequisites[i][0];
        var prerequisite = prerequisites[i][1];
        if (edges.has(prerequisite)) {
            edges.get(prerequisite).push(course);
        } else {
            edges.set(prerequisite, [course]);
        }
        indegree[course] += 1;
    }
    var queue = [];
    for (var i = 0; i < numCourses; i++) {
        if (indegree[i] == 0) {
            queue.push(i);
        }
    }
    var orderOfCourses = [];
    while (queue.length > 0) {
        var prerequisite = queue.shift();
        orderOfCourses.push(prerequisite);
        var courseList = edges.get(prerequisite);
        for (var i = 0; i < courseList?.length; i++) {
            indegree[courseList[i]] -= 1;
            if (indegree[courseList[i]] == 0) {
                queue.push(courseList[i]);
            }
        }
    }
    return orderOfCourses.length == numCourses ? orderOfCourses : [];
}
