// function to find the maximum value of a sequence
function f_gold(n) {
    if (n == 0) {
        return 0;
    }
    // create an array of size n+1 and fill it with 0
    var nums = new Array(n + 1).fill(0);
    nums[1] = 1;
    // loop through the array and calculate the values
    for (var i = 2; i <= n; i++) {
        nums[i] = (i % 2 == 0) ? nums[i >> 1] : nums[i >> 1] + nums[(i >> 1) + 1];
    }
    // return the maximum value in the array
    return Math.max(...nums);
}
