function is_goldbach_conjecture_true(c) {
    var a = 0;
    var b = Math.floor(Math.sqrt(c));
    while (a <= b) {
        var sum_of_squares = a * a + b * b;
        if (sum_of_squares == c) {
            return true;
        }
        if (sum_of_squares < c) {
            a += 1;
        }
        else {
            b -= 1;
        }
    }
    return false;
}
```
