function form_square_with_matchsticks(matchsticks) {
    function depth_first_search(index) {
        if (index == matchsticks.length) {
            return true;
        }
        for (let i = 0; i < 4; i++) {
            if (i > 0 && edges[i - 1] == edges[i]) {
                continue;
            }
            edges[i] += matchsticks[index];
            if (edges[i] <= x && depth_first_search(index + 1)) {
                return true;
            }
            edges[i] -= matchsticks[index];
        }
        return false;
    }
    let sum_of_matchsticks = matchsticks.reduce((a, b) => a + b);
    let x = sum_of_matchsticks / 4;
    let mod = sum_of_matchsticks % 4;
    if (mod || x < Math.max(...matchsticks)) {
        return false;
    }
    let edges = [0, 0, 0, 0];
    matchsticks.sort((a, b) => b - a);
    return depth_first_search(0);
}
