```javascript
function findOptimalGoldPiles(piles, k) {
    var prefixSums = piles.map(function (pile) {
        return [0].concat(pile.map(
            (sum => value =>
                sum += value)(0)));
    });
    var dp = new Array(k + 1).fill(0);
    for (var prefixSum of prefixSums) {
        for (var j = k; j >= 0; j--) {
            for (var idx = 0; idx < prefixSum.length; idx++) {
                if (j >= idx) {
                    dp[j] = Math.max(dp[j], dp[j - idx] + prefixSum[idx]);
                }
            }
        }
    }
    return dp[k];
}
```
