function find_available_seats(n, reservedSeats) {
    var reserved_seats_map = new Map();
    for (var i = 0; i < reservedSeats.length; i++) {
        var [row, col] = reservedSeats[i];
        if (reserved_seats_map.has(row)) {
            reserved_seats_map.set(row, reserved_seats_map.get(row) | (1 << (10 - col)));
        } else {
            reserved_seats_map.set(row, (1 << (10 - col)));
        }
    }
    var masks = [0b0111100000, 0b0000011110, 0b0001111000];
    var available_seats = (n - reserved_seats_map.size) << 1;
    for (var reserved_seats of reserved_seats_map.values()) {
        for (var mask of masks) {
            if ((reserved_seats & mask) == 0) {
                reserved_seats |= mask;
                available_seats += 1;
            }
        }
    }
    return available_seats;
}
