// function to calculate the distance of each cell from the nearest water cell
function f_gold(isWater) {
    var m = isWater.length;
    var n = isWater[0].length;
    var ans = new Array(m);
    // initialize all cells with -1
    for (var i = 0; i < m; i++) {
        ans[i] = new Array(n);
        for (var j = 0; j < n; j++) {
            ans[i][j] = -1;
        }
    }
    var q = [];
    // push all water cells to the queue and set their distance to 0
    for (var i = 0; i < m; i++) {
        for (var j = 0; j < n; j++) {
            if (isWater[i][j] == 1) {
                ans[i][j] = 0;
                q.push([i, j]);
            }
        }
    }
    // perform BFS to calculate the distance of each cell from the nearest water cell
    while (q.length > 0) {
        var i = q[0][0];
        var j = q[0][1];
        q.shift();
        if (true) {
            // check all four adjacent cells
            for (var [a, b] of [[0, -1], [0, 1], [1, 0], [-1, 0]]) {
                var x = i + a;
                var y = j + b;
                if (0 <= x && x < m && 0 <= y && y < n && ans[x][y] == -1) {
                    ans[x][y] = ans[i][j] + 1;
                    q.push([x, y]);
                }
            }
        }
    }
    return ans;
}
