// define a function that takes an array of numbers as input and returns a product of two largest numbers minus one
function f_gold(nums) {
    var i = 0;
    // determine which element is larger between the first two elements
    if (nums[0] > nums[1]) i = 0;
    else i = 1;
    // set j to the index of the other element
    var j = 1 - i;
    // iterate through the rest of the array
    for (var k = 2; k < nums.length; k++) {
        // if the current element is larger than the larger element i, swap i and j
        if (nums[k] > nums[i]) {
            j = k;
            [i, j] = [j, i];
            // add a comment here
        }
        // if the current element is larger than the smaller element j, update j
        else if (nums[k] > nums[j]) {
            j = k;
        }
    }
    // return the product of two largest elements minus one
    return (nums[i] - 1) * (nums[j] - 1);
}
