function find_max_unique_chars_substring(arr) {
    function count_ones_in_binary(x) {
        var count = 0;
        while (x) {
            x &= x - 1;
            count += 1;
        }
        return count;
    }
    var max_count = 0;
    var masks = [0];
    for (var str of arr) {
        var mask = 0;
        for (var ch of str) {
            ch = ch.charCodeAt(0) - 'a'.charCodeAt(0);
            if ((mask >> ch) & 1 == 1) {
                mask = 0;
                break;
            }
            mask |= 1 << ch;
        }
        if (mask == 0) continue;
        for (var m of masks) {
            if ((m & mask) == 0) {
                masks.push(m | mask);
                max_count = Math.max(max_count, count_ones_in_binary(m | mask));
            }
        }
    }
    return max_count;
}
