// function to find the length of the longest subarray with absolute difference between any two elements at most k
function f_gold(nums, k) {
    // sort the array in non-descending order
    nums.sort((a, b) => (a - b));
    // initialize variables
    var ans = 1;
    var window = 0;
    var l = 0;
    var r = 1;
    var n = nums.length;
    // iterate through the array
    while (r < n) {
        // calculate the sum of the differences between adjacent elements in the current window
        window += (nums[r] - nums[r - 1]) * (r - l);
        r += 1;
        // if the sum of differences is greater than k, move the left pointer to the right
        while (window > k) {
            window -= nums[r - 1] - nums[l];
            l += 1;
        }
        // update the answer with the length of the current subarray
        ans = Math.max(ans, r - l);
    }
    // return the answer
    return ans;
}
