const accumulate = (arr) => arr.reduce((a, c) => [...a, c + (a.length > 0 ? a[a.length - 1] : 0)], []);

const minimumAverageDifference = (nums) => {
    const s = accumulate(nums);
    let ans = 0, n = nums.length;
    let mi = Number.POSITIVE_INFINITY;
    for (let i = 0; i < n; i++) {
        let a = Math.floor(s[i] / (i + 1));
        let b = i === n - 1 ? 0 : Math.floor((s[n - 1] - s[i]) / (n - i - 1));
        let t = Math.abs(a - b);
        if (mi > t) {
            ans = i;
            mi = t;
        }
    }
    return ans;
};
