function format_coordinates(s) {
    function convert(start, end) {
        var res = [];
        for (var k = 1; k < end - start + 1; k++) {
            var left = s.substring(start, start + k);
            var right = s.substring(start + k, end);
            var valid = (left == '0' || !left.startsWith('0')) && !right.endsWith('0');
            if (valid) {
                res.push(left + (k < end - start ? '.' : '') + right);
            }
        }
        return res;
    }
    var n = s.length;
    var ans = [];
    for (var i = 2; i < n - 1; i++) {
        for (var x of convert(1, i)) {
            for (var y of convert(i, n - 1)) {
                ans.push('(' + x + ', ' + y + ')');
            }
        }
    }
    return ans;
}
