```javascript
function find_golden_numbers(n) {
    var golden_numbers = [1, 1, 1, 1, 1];
    var MOD = 1000000007;
    for (var i = 0; i < n - 1; i++) {
        golden_numbers = [
            (golden_numbers[1] + golden_numbers[2] + golden_numbers[4]) % MOD,
            (golden_numbers[0] + golden_numbers[2]) % MOD,
            (golden_numbers[1] + golden_numbers[3]) % MOD,
            golden_numbers[2],
            (golden_numbers[2] + golden_numbers[3]) % MOD,
        ];
    }
    return golden_numbers.reduce((a, b) => a + b) % MOD;
}
