function findBlackPixel(picture, target) {
    const m = picture.length;
    const n = picture[0].length;
    const rows = new Array(m).fill(0);
    const cols = new Map();
    for (let i = 0; i < m; i++) {
        for (let j = 0; j < n; j++) {
            if (picture[i][j] === 'B') {
                rows[i] += 1;
                if (!cols.has(j)) {
                    cols.set(j, []);
                }
                cols.get(j).push(i);
            }
        }
    }
    const t = new Array(m).fill().map(() => new Array(m).fill(false));
    for (let i = 0; i < m; i++) {
        for (let k = i; k < m; k++) {
            if (i === k) {
                t[i][k] = true;
            } else {
                t[i][k] = picture[i].every((val, j) => val === picture[k][j]);
            }
            t[k][i] = t[i][k];
        }
    }
    let res = 0;
    for (let i = 0; i < m; i++) {
        if (rows[i] === target) {
            for (let j = 0; j < n; j++) {
                if (cols.has(j) && cols.get(j).length === target && cols.get(j).every(k => t[i][k])) {
                    res += 1;
                }
            }
        }
    }
    return res;
}
