"use strict";

function minFlipsMonoIncr(s) {
    const n = s.length;
    const left = new Array(n + 1).fill(0);
    const right = new Array(n + 1).fill(0);
    let ans = Infinity;
    for (let i = 1; i <= n; i++) {
        left[i] = left[i - 1] + (s[i - 1] === '1' ? 1 : 0);
    }
    for (let i = n - 1; i >= 0; i--) {
        right[i] = right[i + 1] + (s[i] === '0' ? 1 : 0);
    }
    for (let i = 0; i <= n; i++) {
        ans = Math.min(ans, left[i] + right[i]);
    }
    return ans;
}
