function find_maximum_subarray_sum(nums) {
    var cumulative_sum = [0].concat(nums.map((sum => v => sum += v)(0)));
    return Math.max.apply(Math, cumulative_sum.slice(1).map(function (val, i) { return Math.max(val, cumulative_sum[cumulative_sum.length - 1] - cumulative_sum[i]); }));
}
