function find_maximum_subarray_sum(nums) {
    var max_sum_so_far = nums[0], min_sum_so_far = nums[0], max_ending_here = nums[0], min_ending_here = nums[0];
    for (var num of nums.slice(1)) {
        max_ending_here = num + Math.max(max_ending_here, 0);
        min_ending_here = num + Math.min(min_ending_here, 0);
        max_sum_so_far = Math.max(max_sum_so_far, max_ending_here);
        min_sum_so_far = Math.min(min_sum_so_far, min_ending_here);
    }
    return max_sum_so_far <= 0 ? max_sum_so_far : Math.max(max_sum_so_far, nums.reduce((a, b) => a + b) - min_sum_so_far);
}
